import React from "react";
import { makeStyles, Typography } from "@material-ui/core";
const useStyles = makeStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    // justifyContent: "center",
    // alignItems: "center",
    width: "300px",
    height: "350px",
    background: "transparent",
    border:"1px solid black",
  },
  image:{
      width:"100%",
      height:"50%",
  },
  name:{
      color:"gray",
      fontSize:"21px"
  }
});
const Index = () => {
  const classes = useStyles();
  const dummy = {
    iamge: "https://pbs.twimg.com/media/CV4VEzDXIAIdgXG?format=jpg&name=large",
    name: "john",
    followers: 1002,
    rate: 5,
  };
  return (
      <div className={classes.root}>
          <img src={dummy.iamge} className={classes.image}/>
          <Typography className={classes.name} >{dummy.name}</Typography>
      </div>
  )
};

export default Index;
