import { Grid } from "@material-ui/core";
import axios from "axios";
import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import Card from "./Card/index"
const Index = () => {
  const [arr, setarr] = useState([]);
  const { state } = useLocation();
  useEffect(() => {
    axios.get(`https://api.spotify.com/v1/artists/${state.id}/albums`, {
      headers: { Authorization: `Bearer ${state.token}` },
    })
    .then(res=>{
        setarr(res.data.items)
        console.log(res.data.items)
    })
    .catch(err=>{
        console.log(err)
    })
  }, []);
  return (
      <div>
    <div style={{display:"flex",alignItems:"center",justifyContent:"center"}}>
        <Grid container spacing={3} style={{ width: "80%", marginTop: "2%" }}>
        {arr.length>0 && arr.map((i, index) => {
          return (
              <Grid item md={3} sm={6} xs={12}>
                <Card
                  img={i.images}
                  id={i.id}
                  token={state.token}
                  albumName={i.name}
                  numberOfTracks={i.total_tracks}
                  date={i.release_date}
                  artistName={state.name}
                />
              </Grid>
          );
        })}
      </Grid>
    </div>
    </div>
  );
};

export default Index;
