import React, { useEffect, useState } from "react";
import { Grid, makeStyles } from "@material-ui/core";
import SearchIcon from "@material-ui/icons/Search";
import Card from "./Card/index";
import { Link, useLocation } from "react-router-dom";
import Cookies from "js-cookie";
import axios from "axios";
const useStyle = makeStyles({
  root: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "column",
    marginTop: "2%",
  },
  searchContainer: {
    width: "35%",
    height: "45px",
    background: "white",
    border: "1px solid lightgray",
    borderRadius: "10px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  search: {
    height: "45px",
    width: "75%",
    outline: "none",
    border: "none",
    textAlign: "center",
    fontSize: "22px",
    background: "transparent",
  },
});
const Index = (props) => {
  const classes = useStyle();
  const [arr, setarr] = useState([]);
  const [item,setItem]=useState("tom")
  const {state}=useLocation();
  useEffect(() => {
    axios.get(
      `https://api.spotify.com/v1/search?type=artist&q=${item}`,
      { headers: {Authorization:`Bearer ${state}`} }
    ).then(res=>{
        //const result=res.data.filter(i=>i.name.toLowerCase().startWith(item.toLowerCase()));
        setarr(res.data.artists)
        console.log(res.data.artists.items)
    })
    .catch(err=>{
        console.log(err)
    })
  }, [item]);
  
  return (
    <div className={classes.root}>
      <div className={classes.searchContainer}>
        <input
          type="text"
          placeholder="search for an artist"
          className={classes.search}
        />
        <SearchIcon style={{ marginRight: "10px" }} />
      </div>
      <Grid container spacing={3} style={{ width: "80%", marginTop: "2%" }}>
        {arr.map((i, index) => {
          return (
            // <Link to={`/home/${i.name}`}>
              <Grid item md={3} sm={6} xs={12}>
                <Card
                  img={i.image[0]}
                  name={i.name}
                  followers={i.followers}
                  rate={5}
                />
              </Grid>
            // </Link>
          );
        })}
      </Grid>
    </div>
  );
};

export default Index;
