  const [arr, setarr] = useState([]);
  const { state } = useLocation();
  useEffect(() => {
    axios.get(`https://api.spotify.com/v1/artists/${state.id}/albums`, {
      headers: { Authorization: `Bearer ${state.token}` },
    })
    .then(res=>{
        setarr(res.data.items)
    })
    .catch(err=>{
        console.log(err)
    })
  }, []);
  return (
    <div>
      <h1>sd</h1>
    </div>
  );
};

export default Index;
