import React from "react";
import { makeStyles, Typography } from "@material-ui/core";
import { Rating } from '@material-ui/lab'
const useStyles = makeStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    // justifyContent: "center",
    // alignItems: "center",
    width: "250px",
    height: "350px",
    background: "transparent",
    border:"1px solid black",
  },
  image:{
      width:"100%",
      height:"60%",
  },
  name:{
      color:"black",
      fontSize:"21px",
      marginTop:"5%",
      marginLeft:"5%",
      fontWeight:"400"
  },
  followers:{
      color:"gray",
      fontSize:"12px",
      marginLeft:"5%",
      fontWeight:"300"
  },

});
const Index = () => {
  const classes = useStyles();
  const dummy = {
    iamge: "https://pbs.twimg.com/media/CV4VEzDXIAIdgXG?format=jpg&name=large",
    name: "Tom Petty",
    followers: 1002,
    rate: 5,
  };
  const formatFollowers=(n)=>{
    let nf=Intl.NumberFormat();
    return nf.format(n)
  }
  return (
      <div className={classes.root}>
          <img src={dummy.iamge} className={classes.image}/>
          <Typography className={classes.name} >{dummy.name}</Typography>
          <Typography className={classes.followers} >{formatFollowers(dummy.followers)} followers</Typography>
        <Rating defaultValue={dummy.rate} disabled={true} />
      </div>
  )
};

export default Index;
