import React ,{useState,useEffect}from "react";
import { Icon } from '@iconify/react';
import { Grid, makeStyles, TextField,InputAdornment, Button } from "@material-ui/core";
const useStyle = makeStyles({
  root: {
    width: "40%",
  },
});
const Index = () => {
  const classes = useStyle();
  const CLIENT_ID="0ffcec7caf3b459198766c0a334e7232";
  const REDIRECT_URL="https://localhost:3000/"
  const AUTH_ENDPOINT="http://accounts.spotify.com/authorize"
  const RESPONSE_TYPE="token"
  useEffect(() => {
    const hash = window.location.hash
    let token = window.localStorage.getItem("token")

    // getToken()


    if (!token && hash) {
        token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]

        window.location.hash = ""
        window.localStorage.setItem("token", token)
    }

    setToken(token)

}, [])
  return (
    <Grid
      container
      justifyContent="center"
      alignItems="center"
      style={{ minHeight: "100vh" }}
    >
      <Button
        color="primary"
        variant="outlined"
        InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <Icon icon="mdi:spotify" color="#1db954" width={30} />
              </InputAdornment>
            ),
          }}
        className={classes.root}
        placeholder="Login with spotify"
      />
    </Grid>
  );
};

export default Index;
